import React, { useState,useEffect, useContext } from "react";
import {Modal,FloatingLabel,Form,Row,Col,ToggleButton,ButtonGroup,Container} from 'react-bootstrap';
import {Button} from 'react-bootstrap';
import Axios from "axios";
import {useHistory} from "react-router-dom";
import addPost from "./addPost.css"
function AddPost(props){
  const [artType,setartType]=useState('artwork');
  const [photoURL,setPhotoURL]=useState(null);
  const [isLoggedIn, setLoggedIn]=useState(false);
  const [loggedInUser,setLoggedInUser]=useState('');
  const history=useHistory();
  useEffect(()=>{
    Axios.get("/api/login").then((response)=>{
      if(response.data.loggedIn==true){
        console.log(response.data.user);
        setLoggedIn(true);
        setLoggedInUser(response.data.user);
      };
    })
  },[]);
  const type=[
    {id:'1',value:'artwork'},
    {id:'2',value:'photography'}
  ]

  function uploadImage(e){
    setPhotoURL(URL.createObjectURL(e.target.files[0]));
  }
  function upload(){

    let post={
      title:document.getElementById('photoID').value,
      description:document.getElementById('description').value,
      artType:artType,
      photoURL:photoURL,
      userinfo:loggedInUser
    }
    console.log(post);
    Axios.post('/posts',post).then(function(response){
      console.log(response);

    });
    props.onHide();
    history.push("/");
  }
    return (
    <Modal
    {...props}
    size="lg"
    animation={false}
    centered
  >
  <Container>
    <Row>
      <Col>
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter" >
              Add A New Post
            </Modal.Title>
          </Modal.Header>

            <h4>Upload Your Art Here</h4>
      </Col>
      <Col>
      <FloatingLabel  controlId="floatingTextarea" label="Title" className="mb-3">
        <Form.Control id='photoID' as="textarea" placeholder="Leave a comment here" />
      </FloatingLabel>
      </Col>
    </Row>


    <FloatingLabel controlId="floatingTextarea2" label="Description">
      <Form.Control
        id='description'
        as="textarea"
        placeholder="Leave a comment here"
        style={{ height: '100px' }}
      />
      </FloatingLabel>
  <fieldset>
    <Form.Group as={Row} className="mb-3">
        <Form.Check
          type="radio"
          label="Upload an Artwork"
          name="artwork"
          id="artwork"
        />
        <Form.Check
          type="radio"
          label="Upload a Photography"
          name="photography"
          id="photography"
        />
    </Form.Group>
    //ButtonGroup was here
    <Form.Group>
    <input type="file" id='uploadedWork' className="form-control" onChange={uploadImage} />
    </Form.Group>
  </fieldset>

    <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
        <Button onClick={upload} type='submit'>Upload</Button>
    </Modal.Footer>
  </Container>
   </Modal>);
}
export default AddPost;
