import React, { useState, useContext } from "react";
// import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import FormGroup from "@material-ui/core/FormGroup";
import Card from "@material-ui/core/Card";
import Link from "@material-ui/core/Link";
import { makeStyles } from "@material-ui/core/styles";
// import Axios from "axios";
// import { SessionContext, setSessionCookie } from "../components/UserContext";
// import { useHistory } from "react-router";

function Login() {
  var UserisRegistered = false;
  const [isRegistered, setRegisterState] = useState(true);
  const [UserName, setUserName] = useState("");
  const [password, setPassword] = useState("");
  // const history = useHistory();
  // const { setSession } = useContext(SessionContext);
  function handleClicked(){
    setRegisterState(!isRegistered);
  }
  return (
    <div className="root">
      <Card className="card">
        <Container className='loginSquare' maxWidth="xs">
          {/* <form
            onSubmit={(e) => {
              signUp(e);
            }}
          > */}
            <FormGroup>
              <h1 className="title">
                {isRegistered ? "Log in" : "Register"}
              </h1>
              {!isRegistered ? (
                <div>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      type="text"
                      id="firstname"
                      placeholder="名"
                      required
                      autofocus
                    >
                      {" "}
                    </TextField>
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      type="text"
                      id="lastname"
                      placeholder="姓"
                      required
                      autofocus
                    >
                      {" "}
                    </TextField>
                  </Grid>
                </div>
              ) : null}
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  type="text"
                  id="username"
                  placeholder="Email"
                  name="username"
                  label="email"
                  required
                  autofocus
                ></TextField>
              </Grid>
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="password"
                  name="password"
                  size="small"
                  type="password"
                  id="password"
                />
              </Grid>
              <Grid item xs={12}>
                <Button
                  className={Button}
                  color="primary"
                  variant="outlined"
                  fullWidth
                  type="submit"
                >
                  {isRegistered ? "Login" : "Register"}
                </Button>
              </Grid>
              <Grid item xs={12}>
                <div className="link">
                  <Link onClick={handleClicked}>
                    {isRegistered ? "Register" : "Login"}
                  </Link>
                </div>
              </Grid>
            </FormGroup>
          {/* </form> */}
        </Container>
      </Card>
    </div>
  );
}
export default Login;
