import { Image } from "./image";
import plus from './../plus.png';
import {Button} from 'react-bootstrap';
import React, { useState, useContext } from "react";
import AddPost from './addPost';
export const Gallery = (props) => {
  const [addState,setAddState]=useState(false);
  return (
    <div id='portfolio' className='text-center'>
      <div className='container'>
        <div className='section-title'>
          <h2>Gallery</h2>
          <p>
          To help an artist gather and give genuine critique anonymously from other artists<br></br>in order to replenish creativity and create better art
          </p>
        </div>
        <div className='row'>
        <div className='portfolio-items'>
        <div className='col-sm-6 col-md-4 col-lg-4'>

          <Button >
          <img style={{width:'10%'}} onClick={()=>setAddState(true)} src={plus} style={{position:'relative'}}/>
          </Button>
          </div>



            {props.data
              ? props.data.map((d, i) => (
                <div key={`${d.title}-${i}`} className='col-sm-6 col-md-4 col-lg-4'>
                  <Image title={d.title} largeImage={d.largeImage} smallImage={d.smallImage} />
                </div>
              ))
              : 'Loading...'}
          </div>
        </div>
      </div>

    </div>
  )
}
